mapper.select:
select count(*) from poly_employee
and employee_no = #{employeeNo}
and employee_name like concat('%',#{employeeName},'')
and department_id = #{departmentId}

select e.*, r.`id` as rid, r.`name`, r.`name_zh`, r.`gmt_create`, r.`gmt_modified` from poly_employee e, poly_employee_role er, poly_role r
e.`id` = er.`eid` and er.`rid` = r.`id`
and employee_no = #{employee.employeeNo}
and employee_name = #{employee.employeeName}
and department_id = #{employee.departmentId}
limit #{startIndex},#{pageSize}
